using System.Collections;using System.Collections.Generic;using UnityEngine;[CreateAssetMenu(fileName = "Data", menuName = "ScriptableObjects/CharacterObjectScriptableObject", order = 1)]public class CharacterObject : ScriptableObject{    [SerializeField] public bool isAlive = true;    [SerializeField] public string charName = "bob";    [SerializeField] public int charID = 0;    [SerializeField] public string sexPref = "Male";    [SerializeField] public string charGender = "Male";    [SerializeField] public bool isPoly = false;    public GameObject relationshipManager = null;    private void Awake()    {        RChar r = new RChar(isAlive, charName, charID, sexPref, charGender, isPoly);        relationshipManager = GameObject.FindGameObjectWithTag("RelationshipManager");        relationshipManager.GetComponent<RelationshipManager>().charList[charID] = r;    }}