using System.Collections;using System.Collections.Generic;using UnityEngine;public class RChar{    public bool isAlive = true;    public string charName = "bob";    public int charID = 0;    //For checking marriage / courtship depending on target's gender    public string sexPref = "Male";    public string charGender = "Male";    //For multiple marriages / mistresses    public bool isPoly = false;    public RChar(bool risAlive, string rcharName, int rcharID, string rsexPref, string rcharGender, bool risPoly)    {        isAlive = risAlive;        charName = rcharName;        charID = rcharID;        sexPref = rsexPref;        charGender = rcharGender;        isPoly = risPoly;    }}